import {loadJokes} from '../Graphqlservices/requests';

 const loadJoke=  (state='JustTestt',action)=>
{
    switch (action.type) {
        case 'LOADJOKE':
                return {
                   loadaJoke: function loadJokes(cate)
                                        {
                                        const  response=  fetch(endpointBaseURL,{
                                                method: 'POST',
                                                headers:{'content-type':'application/json'},
                                                body: JSON.stringify({
                                                    query:` 
                                                    query JokesQuery($cate: String){
                                                        jokes(cat:$cate){
                                                        value
                                                        }
                                                    }                 
                                                    `,
                                                    variables:{cate}
                                                })
                                            });

                                            const responseBody= response.json();

                                            return responseBody.data.jokes.value;
                                        }
                }
                
            break;
    
        default:
           return state;
    }
}
export default loadJoke;
