import React,{Component} from 'react';
import homeicon from '../../assests/home.png';
import { loadJoke } from '../../Graphqlservices/requests';
import './ViewcategoryJoke.css';
import ClipLoader from 'react-spinners/CircleLoader';
import { css } from "@emotion/core";

class ViewcategoryJoke extends Component{
   
    //css for spinner
     override = css`
        display: block;
        margin: 0 auto;
        border-color: red;
        `;
         
    
    constructor(props){
        super(props);
        this.state={joke:'',loading:false,color:"#ffffff"};

    }
 
   async componentDidMount()
   {
       const cateparam=this.props.match.params.cat;
       const joke= await loadJoke(cateparam);
       this.setState({joke:joke,loading:true})
     
   }
render() {
  return (
    
        <div className="categoryjoke">
          <h1>{this.props.match.params.cat}</h1>
          <div className="para">
            <p>{this.state.joke}</p>
          </div>
          <a href={'/'}><img className="homeicon" src={homeicon}/></a>
          <ClipLoader color={this.color} loading={this.loading} css={this.override} size={150} />
        </div>
   
  );
}
}
export default ViewcategoryJoke;
