import {loadJokes} from '../Graphqlservices/requests';
const endpointBaseURL='http://localhost:5000/graphql';






const loadJoke= async (state='',action)=>
{
    switch (action.type) {
        case 'LOADJOKE':
                  return state= await loadJokes('money');
                                      
            break;
    
        default:
           return state;
    }
}
export default loadJoke;
