import React,{Component} from 'react';
import { loadJoke } from '../../Graphqlservices/requests';
import './ViewcategoryJoke.css'

class ViewcategoryJoke extends Component{
   
    
    constructor(props){
        super(props);
        this.state={joke:''};

    }
 
   async componentDidMount()
   {
       const cateparam=this.props.match.params.cat;
       const joke= await loadJoke(cateparam);
       this.setState({joke})
     
   }
render() {
  return (
    
        <div className="categoryjoke">
          <h1>{this.props.match.params.cat}</h1>
          <p>{this.state.joke}</p>
        </div>
   
  );
}
}
export default ViewcategoryJoke;
